
function getRandomValues() {
  var values = new Array(20);
  for (var i = 0; i < values.length; i++) {
    values[i] = [5 + randomVal(), 10 + randomVal(), 15 + randomVal(), 20 + randomVal(), 30 + randomVal(), 35 + randomVal(), 40 + randomVal(), 45 + randomVal(), 50 + randomVal()]
  }
  return values
}

function showTooltip(x, y, contents) {
  $("<div id='tooltip' class='flot-tooltip'>" + contents + "</div>").css({
    top: y + 5, left: x + 5
  }).appendTo("body").fadeIn(200)
}

function gt(y, m, d) {
  return new Date(y, m - 1, d).getTime()
}

function randomVal() {
  return Math.floor(Math.random() * 80)
}

function chartWeek(placeholder, visit) {

  var plot = $.plot(placeholder, [{
      data: visit, label: "Visitas", lines: {
        show: true, lineWidth: 2, fill: true
      }, points: {
        show: true, lineWidth: 3, fill: true, fillColor: "#fafafa"
      }
    }], {
    series: {
      lines: {
        lineWidth: 2, fillColor: {
          colors: [{
              opacity: .1
            }, {
              opacity: .1
            }]
        }
      }, points: {
        lineWidth: 3
      }, shadowSize: 0
    }, grid: {
      hoverable: true, clickable: true, borderWidth: 0, tickColor: "#E4E4E4"
    }, colors: ["#E7A13D", "#FF3300"], yaxis: {
      font: {
        color: "#555"
      }, ticks: 8
    }, xaxis: {
      mode: "time", timezone: "browser", minTickSize: [1, "day"], timeformat: "%a", font: {
        color: "#555"
      }, tickColor: "#fafafa", autoscaleMargin: .02
    }, legend: {
      labelBoxBorderColor: "transparent", backgroundColor: "transparent"
    }, tooltip: true, tooltipOpts: {
      content: "%s: %y"
    }
  });
}

